//render a form to create new receipe

app.get('/recipes/new', (req, res) => {
  res.render('new');
})



app.post('/recipes', (req, res) => {
  const { name,category,ingredients,instructions,image } = req.body;
  recipes.push({name,category,ingredients,instructions, image,id: uuid() })
  res.redirect('/recipes');
})


app.get('/recipes/:id', (req, res) => {
  const { id } = req.params;
  const recipe = recipes.find(r=> r.id === id);
  res.render('show', { recipe })
})


app.get('/search', (req, res) => {
  const { category } = req.query;

  if (!category) {
    // Handle the case where no category is provided in the query parameter
    return res.status(400).send('Please provide a category for the search.');
  }

  const filteredRecipes = recipes.filter(recipe => recipe.category === category);
  console.log(filteredRecipes);
 res.render('selected', {filteredRecipes });
});





// EDIT - renders a form to edit a recipe
// *******************************************
app.get('/recipes/:id/edit', (req, res) => {
  const { id } = req.params;
  const recipe = recipes.find(r => r.id === id);
  res.render('edit', { recipe })
})

app.patch('/recipes/:id', (req, res) => {
  const { id } = req.params;
  const foundComment = recipes.find(c => c.id === id);

  //get new text from req.body
  const newCategory = req.body.category;
  const newName=req.body.name;
  const newIngredient=req.body.ingredients;
  const newInstructions=req.body.instructions;
  const newImage=req.body.image;



  console.log(req.body);
  //update the comment with the data from req.body:
  foundComment.category = newCategory;
  foundComment.name = newName;
  foundComment.category = newCategory;
  foundComment.ingredients = newIngredient;
  
  foundComment.image = newImage;
  
  
  //redirect back to index (or wherever you want)
  res.redirect('/recipes')
})




// *******************************************
// DELETE/DESTROY- removes a recipe
// *******************************************
app.delete('/recipes/:id', (req, res) => {
  const { id } = req.params;
  recipes = recipes.filter(c => c.id !== id);
  res.redirect('/recipes');
})


